import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';


const imagens = [
  require('/home/gabriel/Documentos/my-app/src/img/dado1.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado2.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado3.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado4.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado5.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado6.png'),
  require('/home/gabriel/Documentos/my-app/src/logo.svg'),


];



function App() {

      const [index, setIndex] = useState(6); 

      const [index2, setIndex2] = useState(6); 


      const [suaVezPlayer1, setSuaVezPlayer1] = useState(true); 
      const [suaVezPlayer2, setSuaVezPlayer2] = useState(false); 


      const [Player1Jogou, setPlayer1Jogou] = useState(1);
      const [Player2Jogou, setPlayer2Jogou] = useState(1);


      const [eleGanhou, setEleGanhou] = useState(""); 


      const [girou, setGirou] = useState("1"); 



      var r = "rodada";
      var rodada = 1;
      const verificar = () =>{
        if(girou==2){
          setGirou(0)
          ganhou()
        }
      }

      const girarDado = () => {
        setGirou(girou+1)


        if(suaVezPlayer1){
          setIndex(Math.floor(Math.random() * (imagens.length - 1 )));
          setPlayer1Jogou(Player1Jogou + 1);        
        }
        else if(suaVezPlayer2){
          setIndex2(Math.floor(Math.random() * (imagens.length - 1)));
          setPlayer2Jogou(Player2Jogou + 1);


        }

        suaVez();



      };




      function suaVez() {
        if (suaVezPlayer1) {
          setSuaVezPlayer1(false); 
          setSuaVezPlayer2(true); 
        } else if(setSuaVezPlayer2){
          setSuaVezPlayer1(true); 
          setSuaVezPlayer2(false); 

        }

      }

      function ganhou(){
        if(index > index2  && Player1Jogou == Player2Jogou){
            setEleGanhou( "Player1 ganhou!")
        }
        else if(index2 > index  && Player1Jogou == Player2Jogou){
            setEleGanhou( "Player2 ganhou!")

       }
       else if(index == index2  && Player1Jogou == Player2Jogou){
        setEleGanhou( "Empate")
       }
      }




  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <h1>{r} {rodada} {verificar}</h1>
        <h2>{eleGanhou}</h2>

        <div id='img'>
          <img src={imagens[index]} />
        </div>
        <button onClick={girarDado} disabled={suaVezPlayer1}>girar</button>
        <div id='img2'>
          <img src={imagens[index2]} />
        </div>
        <button onClick={girarDado} disabled={suaVezPlayer2}>girar</button>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}





export default App;
