import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';


const imagens = [
  require('/home/gabriel/Documentos/my-app/src/img/dado1.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado2.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado3.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado4.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado5.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado6.png'),
  require('/home/gabriel/Documentos/my-app/src/logo.svg'),


];



function App() {
      const [index, setIndex] = useState(6); 

      const [suaVezPlayer1, setSuaVezPlayer1] = useState(true); 
      const [suaVezPlayer2, setSuaVezPlayer2] = useState(false); 


      var jogou = 0;

      var rodada = 1;


      const girarDado = () => {
        suaVez();
        setIndex(Math.floor(Math.random() * (imagens.length - 1 )) + 1);
        jogou ++;
      };


      function suaVez() {
        if (suaVezPlayer1) {
          setSuaVezPlayer1(false); 
          setSuaVezPlayer2(true); 
        } else if(setSuaVezPlayer2){
          setSuaVezPlayer1(true); 
          setSuaVezPlayer2(false); 
        }
      }




  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>

        <h1>rodada{rodada}</h1>

        <div id='img'>
          <img src={imagens[index]} />
        </div>
        <button onClick={girarDado} disabled={suaVezPlayer1}>girar</button>
        <div id='img2'>
          <img src={imagens[index]} />
        </div>
        <button onClick={girarDado} disabled={suaVezPlayer2}>girar</button>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}





export default App;
