import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';


const imagens = [
  require('/home/gabriel/Documentos/my-app/src/img/dado1.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado2.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado3.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado4.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado5.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado6.png'),

];



function App() {
      var img = imagens[0];
      const [index, setIndex] = useState(0); // Estado para armazenar o índice da imagem atual

      const girarDado = () => {
        setIndex((prevIndex) => (prevIndex + 1) % imagens.length);
      };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <div id='img'>
        <img src={imagens[index]} alt={`Dado ${index + 1}`} />

        </div>
        <div id='img2'>
        <img src={imagens[index]} alt={`Dado ${index + 1}`} />
        </div>
        <button onClick={girarDado}>girar</button>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}





export default App;
