import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';


const imagens = [
  require('/home/gabriel/Documentos/my-app/src/img/dado1.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado2.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado3.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado4.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado5.png'),
  require('/home/gabriel/Documentos/my-app/src/img/dado6.png'),
  require('/home/gabriel/Documentos/my-app/src/logo.svg'),


];





function App() {
  const [index, setIndex] = useState(6); // Dado do Jogador 1
  const [index2, setIndex2] = useState(6); // Dado do Jogador 2

  const [suaVezPlayer1, setSuaVezPlayer1] = useState(true);
  const [suaVezPlayer2, setSuaVezPlayer2] = useState(false);

  const [player1Jogadas, setPlayer1Jogadas] = useState(0); // Total de jogadas do Jogador 1
  const [player2Jogadas, setPlayer2Jogadas] = useState(0); // Total de jogadas do Jogador 2

  const [vencedor, setVencedor] = useState(null); // Texto do vencedor (ou null)

  const [jogadas, setJogadas] = useState(0); // Contador total de jogadas (ambos os jogadores)

  const girarDado = () => {
    setJogadas(jogadas + 1); // Incrementar total de jogadas

    if (suaVezPlayer1) {
      setIndex(Math.floor(Math.random() * (imagens.length - 1)));
      setPlayer1Jogadas(player1Jogadas + 1);
    } else if (suaVezPlayer2) {
      setIndex2(Math.floor(Math.random() * (imagens.length - 1)));
      setPlayer2Jogadas(player2Jogadas + 1);

      if (jogadas >= 2) { // Verificar vencedor ap칩s ambos jogarem 2 vezes
        const winner = verificarVencedor(index, index2, player1Jogadas, player2Jogadas);
        setVencedor(winner);
      }
    }

    // Inverter a vez (simplificado usando o operador tern치rio)
    setSuaVezPlayer1(!suaVezPlayer1);
    setSuaVezPlayer2(!suaVezPlayer2);
  };

  const verificarVencedor = (dado1, dado2, jogadas1, jogadas2) => {
    if (dado1 > dado2 && jogadas1 === jogadas2) {
      return 'Jogador 1 ganhou!';
    } else if (dado2 > dado1 && jogadas1 === jogadas2) {
      return 'Jogador 2 ganhou!';
    } else if (dado1 === dado2 && jogadas1 === jogadas2) {
      return 'Empate!';
    } else {
      return null; // Nenhum vencedor ainda
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>

        <h1>Rodada </h1>
        <h2>{vencedor}</h2>

        <div id="img">
          <img src={imagens[index]} alt="Dado Jogador 1" />
        </div>
        <button onClick={girarDado} disabled={!suaVezPlayer1}>Jogador 1 Girar</button>
        <div id="img2">
          <img src={imagens[index2]} alt="Dado Jogador 2" />
        </div>
        <button onClick={girarDado} disabled={!suaVezPlayer2}>Jogador 2 Girar</button>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
